options.css:
.optly-modal {
  background-color: rgb(0,0,0);
  background-color: rgba(0,0,0,0.4);
  display: none;
  height: 100%;
  left: 0;
  overflow: auto;
  padding-top: 9%;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 1000;
}

.optly-modal-close {
  color: #aaa;
  float: right;
  font-family: Helvetica,Arial,sans-serif;
  font-size: 28px;
  font-weight: bold;
  margin: -2%;
}

.optly-modal-close:hover,
.optly-modal-close:focus {
  color: black;
  cursor: pointer;
  text-decoration: none;
}

.optly-modal-content {
  background-color: #fefefe;
  margin: auto;
  padding: 2% 3% 3% 3%;
  border: 1px solid #888;
  width: 80%;
  max-width: 1100px;
  position: relative;
}

.optly-modal-left {
	margin-top: 5%
}

.optly-modal-left h1 {
 color: black;
 font-size: 3em;
 line-height: 1.2em;
 margin-bottom: 20px;
}

.optly-modal-left h3 {
 font-size: 1.5em;
 color:rgba(0,0,0,0.5);
 margin-bottom: 20px;
}

.optly-modal-right img {
 margin-top: 0%;
 max-height:90%;
 max-width:90%;
 border: 1px solid transparent;
 border-radius: 8px;
}

.grid {
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-flex-wrap: wrap;
  -ms-flex-wrap: wrap;
  flex-wrap: wrap;
  margin-left: -20px; }

  .grid__cell {
    -webkit-flex: 1;
    -ms-flex: 1;
    flex: 1;
    padding-left: 20px; }

  .grid--gutter--narrow {
    margin-left: -10px; }

    .grid--gutter--narrow > .grid__cell {
      padding-left: 10px; }

  .grid--gutter--wide {
    margin-left: -40px; }

    .grid--gutter--wide > .grid__cell {
      padding-left: 40px; }

  .grid--flush {
    margin-left: 0; }

    .grid--flush > .grid__cell {
      padding-left: 0; }

  .grid--natural > .grid__cell {
    -webkit-flex: none;
    -ms-flex: none;
    flex: none; }

  .grid--center {
    -webkit-align-items: center;
    -ms-flex-align: center;
    -ms-grid-row-align: center;
    align-items: center; }

  .grid--bottom {
    -webkit-align-items: flex-end;
    -ms-flex-align: end;
    -ms-grid-row-align: flex-end;
    align-items: flex-end; }

  .grid__cell[class*="width-"] {
    -webkit-flex: none;
    -ms-flex: none;
    flex: none; }


.button {
  display: inline-block;
  vertical-align: middle;
  white-space: nowrap;
  font-family: inherit;
  cursor: pointer;
  margin: 0;
  line-height: 32px;
  border-width: 1px;
  border-style: solid;
  font-size: 13px;
  font-weight: 400;
  border-radius: 2px;
  height: 34px;
  padding: 0 15px;
  background-color: #F6F6F7;
  border-color: #CDD0D8;
  color: black; }

  .button:visited {
    color: #262626; }

  .button:hover {
    color: #262626;
    box-shadow: 1px 1px 2px 0 rgba(0, 0, 0, 0.16); }

  .button.is-active,
  .button:active {
    background: #64738A;
    border-color: #3B5871;
    color: #FFF;
    box-shadow: inset 1px 0 3px 0 rgba(0, 0, 0, 0.5);
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.5); }

  .button--highlight {
    background-color: #0F75B2;
    border-color: #0E699F;
    color: #FFF;
    text-shadow: 1px 0 1px rgba(0, 0, 0, 0.2); }

    .button--highlight:hover {
      background: #0081BA;
      border-color: #0176A9; }

    .button--highlight:active {
      background: #0074B4;
      border-color: #005584;
      box-shadow: inset 0 1px 3px 0 rgba(0, 0, 0, 0.5); }

    .button--highlight:visited,
    .button--highlight:hover,
    .button--highlight:active,
    .button--highlight:focus {
      color: #FFF; }

  .button--outline {
    background-color: transparent;
    border-color: #8D96A8;
    color: #64738A; }

    .button--outline:visited {
      color: #64738A; }

    .button--outline:hover {
      background-color: transparent;
      border-color: #0074B4;
      color: #0074B4;
      text-shadow: none;
      box-shadow: none; }

    .button--outline:active {
      background-color: transparent;
      border-color: #00415D;
      color: #00415D;
      text-shadow: none;
      box-shadow: none; }

  .button--outline-reverse {
    background-color: transparent;
    border-color: #FFF;
    color: #FFF; }

    .button--outline-reverse:visited {
      color: #FFF; }

    .button--outline-reverse:hover {
      background: transparent;
      border-color: #E0E1E5;
      color: #E5E5E9; }

    .button--outline-reverse:active {
      background: transparent;
      border-color: #FFF;
      color: #FFF; }

  .button--danger {
    background-color: #E8464E;
    border-color: #AA161E;
    color: #FFF;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.3); }

    .button--danger:hover {
      background: #F35E65;
      border-color: #AC192B; }

    .button--danger:active {
      background: #CB2D35;
      border-color: #8F192B; }

    .button--danger:visited,
    .button--danger:hover,
    .button--danger:active,
    .button--danger:focus {
      color: #FFF; }

  .button--plain {
    color: #00415D;
    border-color: transparent;
    background-color: transparent;
    text-shadow: none;
    box-shadow: none; }

    .button--plain:hover {
      color: #8D96A8; }

    .button--plain:active {
      color: #00415D; }

    .button--plain:visited,
    .button--plain:hover,
    .button--plain:active,
    .button--plain:focus {
      border-color: transparent;
      background-color: transparent;
      text-shadow: none;
      box-shadow: none; }

  .button--toggle:hover {
    color: #262626; }

  .button--toggle:active,
  .button--toggle.is-active:hover {
    color: #FFF; }

  .button--toggle:not(:hover):not(.is-active) {
    border-color: transparent;
    background-color: transparent; }

  .button--small {
    font-size: 11px;
    font-weight: 400;
    border-radius: 2px;
    line-height: 26px;
    height: 27px;
    padding: 0 7px; }

  .button--large {
    font-size: 15px;
    font-weight: 400;
    border-radius: 2px;
    line-height: 41px;
    height: 42px;
    padding: 0 20px; }

  .button--narrow {
    padding-left: 10px;
    padding-right: 10px; }

  .button--tight {
    padding-left: 5px;
    padding-right: 5px; }

  .button--full {
    width: 100%;
    padding-right: 0;
    padding-left: 0;
    text-align: center; }

  .button--icon {
    line-height: 1.4; }

    .button--icon .icon {
      vertical-align: sub; }

  .button[disabled],
  .button--disabled,
  .button--disabled:visited,
  .button--disabled:hover,
  .button--disabled:active,
  .button--disabled:focus {
    cursor: default;
    box-shadow: none;
    background-color: #EFF0F2;
    border-color: #E5E5E9;
    color: #a6a6a6;
    text-shadow: none;
    box-shadow: none; }

    .button[disabled].button--plain,
    .button--disabled.button--plain,
    .button--disabled:visited.button--plain,
    .button--disabled:hover.button--plain,
    .button--disabled:active.button--plain,
    .button--disabled:focus.button--plain {
      background-color: transparent;
      border-color: transparent; }

    .button[disabled].button--outline-reverse,
    .button--disabled.button--outline-reverse,
    .button--disabled:visited.button--outline-reverse,
    .button--disabled:hover.button--outline-reverse,
    .button--disabled:active.button--outline-reverse,
    .button--disabled:focus.button--outline-reverse {
      background-color: transparent; }

options.apply_js:
var utils = window.optimizely.get("utils");

var hidePopup = function() {
  document.querySelector('.optly-modal').style.display = "none";
	// close modal event 
};

var showPopup = function() {
	document.querySelector('.optly-modal').style.display = "block";
};

utils.waitForElement('body').then(function(element) {
  var html = widget.$html;
  element.insertAdjacentHTML('beforeend', html);

  document.querySelector('.optly-modal').addEventListener('click', function() {
    hidePopup();
  });

	var pop = setTimeout(function(){
    showPopup();
  }, widget.timer*1000);
});
